import React, { Component } from 'react';
class ChildComponentEvent extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
           data: 'Initial data...'
        }
        this.updateState = this.updateState.bind(this);
     };
     updateState(e) {
        this.setState({data: e.target.value});
     }
     render() {
        return (
           <div>
              <Content myDataProp = {this.state.data} 
                 updateStateProp = {this.updateState}>

                 </Content>
           </div>
        );
     }
  }


class Content extends React.Component {
   constructor(props) {
      super(props);
      console.log("Person Prop",this.props.myDataProp);
   }
    render() {  
        return (  
            <div>  
                <h1>State & Props Example</h1>  
                <h3>Welcome to {this.props.empName}</h3>  
                <button className = "btn btn-primary btm-sm m-2" onClick = {() => this.props.onChange('Arun') } > change Name</button>
                </div>    
        );  
     }  
}


export default ChildComponentEvent;
