import React, { Component } from 'react';

class Person extends React.Component {

    constructor(props) {  
        super(props);  
        this.state = {  
           name: "Saravanan",         
        }  
     } 
handleChange = () => {

   console.log("I am clicked from child component");

   this.setState({ name: "Saravanan", })

 } 
     render() {  
        return (  
           <div>  
              <Employee 
                  empName = {this.state.name}
                  onChange = {this.handleChange}
              />             
           </div>  
        );  
     }  
}

class Employee extends React.Component {
   constructor(props) {
      super(props);
      console.log("Person Prop",this.props.empName);
   }
    render() {  
        return (  
            <div>  
                <h1>State & Props Example</h1>  
                <h3>Welcome to {this.props.empName}</h3>  
                <button className = "btn btn-primary btm-sm m-2" onClick = {this.props.onChange} > change Name</button>
                </div>    
        );  
     }  
}

export default Person;
