import React, {Component} from 'react';
class Counter extends Component {

    state = {
        count : 0
    }
    styles = {
        fontSize : '40px',
        fontWeight : 'bold'
    }
    render(){
        classes = "badge m-2 badge-";
        classes += this.state.count === 0 ? "warning" : "primary" ;
        return (
            <div>
                <span  className = {clas} >{this.formatCount()}</span><p></p>
                <button className= "btn btn-secondary btm-sm m-2" onClick={this.doIncrement}> Increase </button> 
                <button className= "btn btn-secondary btm-sm m-2" onClick={this.doDecrement}> Decrease </button> 
            </div>
      );
    }

    formatCount(){
        const { count } = this.state;
        return (count === 0 ? 'Zero' : count );
    }
    doIncrement = () => {
          this.setState({count : this.state.count + 1 });
    } 
    doDecrement = () => {
        this.setState({count : this.state.count - 1 });
     }
}
export default Counter;
