import React, {Component} from 'react';
class Counter extends Component {



    render(){
            const {onIncrement, onDecrement, onDelete, counter  } = this.props;
            console.log('props', this.props);
        return (
            <div>
                {this.props.children}
                <span  className = {this.getBadgeClasses()} >{this.formatCount()}</span>
                <button className= "btn btn-success btm-sm m-2" onClick={() => onIncrement(this.props.counter) }> Increase </button> 
                <button className= "btn btn-primary btm-sm m-2" onClick={() => this.props.onDecrement(this.props.counter)} > Decrease </button>
                <button className= "btn btn-danger btm-sm m-2" onClick={() => this.props.onDelete(this.props.counter.id) }> Remove </button>  
            </div>
      );  
    }    

    getBadgeClasses(){
        let classes = "badge m-2 badge-";
        classes += this.props.counter.value === 0 ? "warning" : "primary";
        return classes;
    }
    formatCount(){
        const { value } = this.props.counter;
        return (value === 0 ? 'Zero' : value );
    }



}
export default Counter;
